import { WeatherContext } from './WeatherContext';
import { memo } from "react";
import axios from 'axios'

function Search() {
    const [city, setCity] = useContext(WeatherContext)
    const [weather, updateWeather] = useContext(WeatherContext)
    const [toggle, setToggle] = useContext(WeatherContext)

    const API_KEY = "61ff2ff2a45475c0d60c3ba5c2a56d10";

    const fetchWeather = async (e) => {
    e.preventDefault();
    const response = 
    await axios.get(`https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}`);
    updateWeather(response.data);
    console.log(response)
};

useEffect(() => {
    console.log("count2 changed!");
  }, [toggle]);

    return (
        <section className="search" data-visible={toggle}>
            <div className="search-wrapper">
                <div className="search-flex">
                    <div className="search-exit" onClick={() => setToggle(!toggle)}><button>X</button></div>
                    <form className="search-city-form">
                    <input 
                    placeholder="search location"
                    onChange={(e) => setCity(e.target.value)}
                    />
                    <button
                    type="submit"
                    onClick={fetchWeather}>SEARCH</button>

                    </form>
                </div>
            </div>
        </section>
    )
}

export default Search;
