import React, { createContext, useState } from "react";

export const WeatherContext = createContext();

export const WeatherProvider = props => {
    const [city, setCity] = useState();
    return (
        <WeatherContext.Provider 
        value={[city, setCity]}
        >
        {props.children}
        </WeatherContext.Provider>
    );

};







